	#ifdef ENABLE_HASH
	size_t CommManipulatorStateCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.id);
		std::vector<ACE_CDR::Double>::const_iterator data_joint_anglesIt;
		for(data_joint_anglesIt=data.joint_angles.begin(); data_joint_anglesIt!=data.joint_angles.end(); data_joint_anglesIt++) {
			boost::hash_combine(seed, *data_joint_anglesIt);
		}
		seed += CommBasicObjects::CommPose3d::generateDataHash(data.pose_tcp);
		seed += CommBasicObjects::CommPose3d::generateDataHash(data.pose_manipulator);
		boost::hash_combine(seed, data.is_valid);
		
		return seed;
	}
	#endif
	
