	void CommManipulatorStateCore::to_ostream(std::ostream &os) const
	{
	  os << "CommManipulatorState(";
	  os << getId() << " ";
	  std::vector<double>::const_iterator joint_anglesIt;
	  std::vector<double> joint_anglesList = getJoint_anglesCopy();
	  for(joint_anglesIt=joint_anglesList.begin(); joint_anglesIt!=joint_anglesList.end(); joint_anglesIt++) {
	  	os << *joint_anglesIt << " ";
	  }
	  getPose_tcp().to_ostream(os);
	  getPose_manipulator().to_ostream(os);
	  os << getIs_valid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommManipulatorStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<id>" << getId() << "</id>";
		std::vector<double>::const_iterator joint_anglesIt;
		std::vector<double> joint_anglesList = getJoint_anglesCopy();
		counter = 0;
		os << indent << "<joint_anglesList n=\"" << joint_anglesList.size() << "\">";
		for(joint_anglesIt=joint_anglesList.begin(); joint_anglesIt!=joint_anglesList.end(); joint_anglesIt++) {
			os << indent << "<joint_angles i=\"" << counter++ << "\">" << *joint_anglesIt << "</joint_angles>";
		}
		os << indent << "</joint_anglesList>";
		os << indent << "<pose_tcp>";
		getPose_tcp().to_xml(os, indent);
		os << indent << "</pose_tcp>";
		os << indent << "<pose_manipulator>";
		getPose_manipulator().to_xml(os, indent);
		os << indent << "</pose_manipulator>";
		os << indent << "<is_valid>" << getIs_valid() << "</is_valid>";
	}
	
	// restore from xml stream
	void CommManipulatorStateCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_joint_anglesList("<joint_anglesList n=\"");
		static const Smart::KnuthMorrisPratt kmp_joint_angles("\">");
		static const Smart::KnuthMorrisPratt kmp_pose_tcp("<pose_tcp>");
		static const Smart::KnuthMorrisPratt kmp_pose_manipulator("<pose_manipulator>");
		static const Smart::KnuthMorrisPratt kmp_is_valid("<is_valid>");
		
		if(kmp_id.search(is)) {
			unsigned int idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_joint_anglesList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			double joint_anglesItem;
			std::vector<double> joint_anglesList;
			kmp_joint_angles.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_joint_angles.search(is)) {
					is >> joint_anglesItem;
					joint_anglesList.push_back(joint_anglesItem);
				}
			}
			setJoint_angles(joint_anglesList);
		}
		if(kmp_pose_tcp.search(is)) {
			CommBasicObjects::CommPose3d pose_tcpItem;
			pose_tcpItem.from_xml(is);
			setPose_tcp(pose_tcpItem);
		}
		if(kmp_pose_manipulator.search(is)) {
			CommBasicObjects::CommPose3d pose_manipulatorItem;
			pose_manipulatorItem.from_xml(is);
			setPose_manipulator(pose_manipulatorItem);
		}
		if(kmp_is_valid.search(is)) {
			bool is_validItem;
			is >> is_validItem;
			setIs_valid(is_validItem);
		}
	}
	
