	const char* ObjectBeliefCore::getCompiledHash()
	{
		return CommObjectRecognitionObjectsIDL::REPO_HASH;
	}
	
	void ObjectBeliefCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for CommBasicObjects::CommPose3d(idl_ObjectBelief.pose)
		CommBasicObjects::CommPose3d::getAllHashValues(hashes);
	}
	
	void ObjectBeliefCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "ObjectBeliefCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommPose3d(idl_ObjectBelief.pose)
		CommBasicObjects::CommPose3d::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t ObjectBeliefCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, std::string(data.type.c_str()));
		boost::hash_combine(seed, data.probability);
		seed += CommBasicObjects::CommPose3d::generateDataHash(data.pose);
		std::vector<ACE_CDR::Double>::const_iterator data_covIt;
		for(data_covIt=data.cov.begin(); data_covIt!=data.cov.end(); data_covIt++) {
			boost::hash_combine(seed, *data_covIt);
		}
		boost::hash_combine(seed, data.validPosePdf);
		
		return seed;
	}
	#endif
	
