	void ObjectBeliefCore::to_ostream(std::ostream &os) const
	{
	  os << "ObjectBelief(";
	  os << getType() << " ";
	  os << getProbability() << " ";
	  getPose().to_ostream(os);
	  std::vector<double>::const_iterator covIt;
	  std::vector<double> covList = getCovCopy();
	  for(covIt=covList.begin(); covIt!=covList.end(); covIt++) {
	  	os << *covIt << " ";
	  }
	  os << getValidPosePdf() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void ObjectBeliefCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<type>" << getType() << "</type>";
		os << indent << "<probability>" << getProbability() << "</probability>";
		os << indent << "<pose>";
		getPose().to_xml(os, indent);
		os << indent << "</pose>";
		std::vector<double>::const_iterator covIt;
		std::vector<double> covList = getCovCopy();
		counter = 0;
		os << indent << "<covList n=\"" << covList.size() << "\">";
		for(covIt=covList.begin(); covIt!=covList.end(); covIt++) {
			os << indent << "<cov i=\"" << counter++ << "\">" << *covIt << "</cov>";
		}
		os << indent << "</covList>";
		os << indent << "<validPosePdf>" << getValidPosePdf() << "</validPosePdf>";
	}
	
	// restore from xml stream
	void ObjectBeliefCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_type("<type>");
		static const Smart::KnuthMorrisPratt kmp_probability("<probability>");
		static const Smart::KnuthMorrisPratt kmp_pose("<pose>");
		static const Smart::KnuthMorrisPratt kmp_covList("<covList n=\"");
		static const Smart::KnuthMorrisPratt kmp_cov("\">");
		static const Smart::KnuthMorrisPratt kmp_validPosePdf("<validPosePdf>");
		
		if(kmp_type.search(is)) {
			std::string typeItem;
			is >> typeItem;
			setType(typeItem);
		}
		if(kmp_probability.search(is)) {
			double probabilityItem;
			is >> probabilityItem;
			setProbability(probabilityItem);
		}
		if(kmp_pose.search(is)) {
			CommBasicObjects::CommPose3d poseItem;
			poseItem.from_xml(is);
			setPose(poseItem);
		}
		if(kmp_covList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			double covItem;
			std::vector<double> covList;
			kmp_cov.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_cov.search(is)) {
					is >> covItem;
					covList.push_back(covItem);
				}
			}
			setCov(covList);
		}
		if(kmp_validPosePdf.search(is)) {
			bool validPosePdfItem;
			is >> validPosePdfItem;
			setValidPosePdf(validPosePdfItem);
		}
	}
	
