	void CommNavigationGraphCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for CommRobotinoObjects::CommNavigationGraphVertex(idl_CommNavigationGraph.vertices)
		CommRobotinoObjects::CommNavigationGraphVertex::getAllHashValues(hashes);
		// get hash value(s) for CommRobotinoObjects::CommNavigationGraphEdge(idl_CommNavigationGraph.edges)
		CommRobotinoObjects::CommNavigationGraphEdge::getAllHashValues(hashes);
	}
	
	void CommNavigationGraphCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommNavigationGraphCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommRobotinoObjects::CommNavigationGraphVertex(idl_CommNavigationGraph.vertices)
		CommRobotinoObjects::CommNavigationGraphVertex::checkAllHashValues(hashes);
		// check hash value(s) for CommRobotinoObjects::CommNavigationGraphEdge(idl_CommNavigationGraph.edges)
		CommRobotinoObjects::CommNavigationGraphEdge::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t CommNavigationGraphCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		std::vector<CommRobotinoObjectsIDL::CommNavigationGraphVertex>::const_iterator data_verticesIt;
		for(data_verticesIt=data.vertices.begin(); data_verticesIt!=data.vertices.end(); data_verticesIt++) {
			seed += CommRobotinoObjects::CommNavigationGraphVertex::generateDataHash(*data_verticesIt);
		}
		std::vector<CommRobotinoObjectsIDL::CommNavigationGraphEdge>::const_iterator data_edgesIt;
		for(data_edgesIt=data.edges.begin(); data_edgesIt!=data.edges.end(); data_edgesIt++) {
			seed += CommRobotinoObjects::CommNavigationGraphEdge::generateDataHash(*data_edgesIt);
		}
		std::vector<ACE_CDR::Double>::const_iterator data_pathWidthIt;
		for(data_pathWidthIt=data.pathWidth.begin(); data_pathWidthIt!=data.pathWidth.end(); data_pathWidthIt++) {
			boost::hash_combine(seed, *data_pathWidthIt);
		}
		boost::hash_combine(seed, data.valid);
		
		return seed;
	}
	#endif
	
