	void CommNavigationGraphCore::to_ostream(std::ostream &os) const
	{
	  os << "CommNavigationGraph(";
	  std::vector<CommRobotinoObjects::CommNavigationGraphVertex>::const_iterator verticesIt;
	  std::vector<CommRobotinoObjects::CommNavigationGraphVertex> verticesList = getVerticesCopy();
	  for(verticesIt=verticesList.begin(); verticesIt!=verticesList.end(); verticesIt++) {
	  	verticesIt->to_ostream(os);
	  }
	  std::vector<CommRobotinoObjects::CommNavigationGraphEdge>::const_iterator edgesIt;
	  std::vector<CommRobotinoObjects::CommNavigationGraphEdge> edgesList = getEdgesCopy();
	  for(edgesIt=edgesList.begin(); edgesIt!=edgesList.end(); edgesIt++) {
	  	edgesIt->to_ostream(os);
	  }
	  std::vector<double>::const_iterator pathWidthIt;
	  std::vector<double> pathWidthList = getPathWidthCopy();
	  for(pathWidthIt=pathWidthList.begin(); pathWidthIt!=pathWidthList.end(); pathWidthIt++) {
	  	os << *pathWidthIt << " ";
	  }
	  os << getValid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommNavigationGraphCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		std::vector<CommRobotinoObjects::CommNavigationGraphVertex>::const_iterator verticesIt;
		std::vector<CommRobotinoObjects::CommNavigationGraphVertex> verticesList = getVerticesCopy();
		counter = 0;
		os << indent << "<verticesList n=\"" << verticesList.size() << "\">";
		for(verticesIt=verticesList.begin(); verticesIt!=verticesList.end(); verticesIt++) {
			os << indent << "<vertices i=\"" << counter++ << "\">";
			verticesIt->to_xml(os, indent);
			os << indent << "</vertices>";
		}
		os << indent << "</verticesList>";
		std::vector<CommRobotinoObjects::CommNavigationGraphEdge>::const_iterator edgesIt;
		std::vector<CommRobotinoObjects::CommNavigationGraphEdge> edgesList = getEdgesCopy();
		counter = 0;
		os << indent << "<edgesList n=\"" << edgesList.size() << "\">";
		for(edgesIt=edgesList.begin(); edgesIt!=edgesList.end(); edgesIt++) {
			os << indent << "<edges i=\"" << counter++ << "\">";
			edgesIt->to_xml(os, indent);
			os << indent << "</edges>";
		}
		os << indent << "</edgesList>";
		std::vector<double>::const_iterator pathWidthIt;
		std::vector<double> pathWidthList = getPathWidthCopy();
		counter = 0;
		os << indent << "<pathWidthList n=\"" << pathWidthList.size() << "\">";
		for(pathWidthIt=pathWidthList.begin(); pathWidthIt!=pathWidthList.end(); pathWidthIt++) {
			os << indent << "<pathWidth i=\"" << counter++ << "\">" << *pathWidthIt << "</pathWidth>";
		}
		os << indent << "</pathWidthList>";
		os << indent << "<valid>" << getValid() << "</valid>";
	}
	
	// restore from xml stream
	void CommNavigationGraphCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_verticesList("<verticesList n=\"");
		static const Smart::KnuthMorrisPratt kmp_vertices("\">");
		static const Smart::KnuthMorrisPratt kmp_edgesList("<edgesList n=\"");
		static const Smart::KnuthMorrisPratt kmp_edges("\">");
		static const Smart::KnuthMorrisPratt kmp_pathWidthList("<pathWidthList n=\"");
		static const Smart::KnuthMorrisPratt kmp_pathWidth("\">");
		static const Smart::KnuthMorrisPratt kmp_valid("<valid>");
		
		if(kmp_verticesList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommRobotinoObjects::CommNavigationGraphVertex verticesItem;
			std::vector<CommRobotinoObjects::CommNavigationGraphVertex> verticesList;
			kmp_vertices.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_vertices.search(is)) {
					verticesItem.from_xml(is);
					verticesList.push_back(verticesItem);
				}
			}
			setVertices(verticesList);
		}
		if(kmp_edgesList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommRobotinoObjects::CommNavigationGraphEdge edgesItem;
			std::vector<CommRobotinoObjects::CommNavigationGraphEdge> edgesList;
			kmp_edges.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_edges.search(is)) {
					edgesItem.from_xml(is);
					edgesList.push_back(edgesItem);
				}
			}
			setEdges(edgesList);
		}
		if(kmp_pathWidthList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			double pathWidthItem;
			std::vector<double> pathWidthList;
			kmp_pathWidth.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_pathWidth.search(is)) {
					is >> pathWidthItem;
					pathWidthList.push_back(pathWidthItem);
				}
			}
			setPathWidth(pathWidthList);
		}
		if(kmp_valid.search(is)) {
			bool validItem;
			is >> validItem;
			setValid(validItem);
		}
	}
	
