	void CommNavigationGraphEdgeCore::to_ostream(std::ostream &os) const
	{
	  os << "CommNavigationGraphEdge(";
	  os << getU() << " ";
	  os << getV() << " ";
	  os << getPath() << " ";
	  os << getWay() << " ";
	  os << getGap() << " ";
	  getType().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommNavigationGraphEdgeCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<u>" << getU() << "</u>";
		os << indent << "<v>" << getV() << "</v>";
		os << indent << "<path>" << getPath() << "</path>";
		os << indent << "<way>" << getWay() << "</way>";
		os << indent << "<gap>" << getGap() << "</gap>";
		os << indent << "<type>";
		getType().to_xml(os, indent);
		os << indent << "</type>";
	}
	
	// restore from xml stream
	void CommNavigationGraphEdgeCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_u("<u>");
		static const Smart::KnuthMorrisPratt kmp_v("<v>");
		static const Smart::KnuthMorrisPratt kmp_path("<path>");
		static const Smart::KnuthMorrisPratt kmp_way("<way>");
		static const Smart::KnuthMorrisPratt kmp_gap("<gap>");
		static const Smart::KnuthMorrisPratt kmp_type("<type>");
		
		if(kmp_u.search(is)) {
			int uItem;
			is >> uItem;
			setU(uItem);
		}
		if(kmp_v.search(is)) {
			int vItem;
			is >> vItem;
			setV(vItem);
		}
		if(kmp_path.search(is)) {
			int pathItem;
			is >> pathItem;
			setPath(pathItem);
		}
		if(kmp_way.search(is)) {
			int wayItem;
			is >> wayItem;
			setWay(wayItem);
		}
		if(kmp_gap.search(is)) {
			double gapItem;
			is >> gapItem;
			setGap(gapItem);
		}
		if(kmp_type.search(is)) {
			CommRobotinoObjects::EDGE_TYPE typeItem;
			typeItem.from_xml(is);
			setType(typeItem);
		}
	}
	
