	void Comm3dPointCloudCore::to_ostream(std::ostream &os) const
	{
	  os << "Comm3dPointCloud(";
	  os << getValid() << " ";
	  std::vector<CommBasicObjects::CommPosition3d>::const_iterator pointsIt;
	  std::vector<CommBasicObjects::CommPosition3d> pointsList = getPointsCopy();
	  for(pointsIt=pointsList.begin(); pointsIt!=pointsList.end(); pointsIt++) {
	  	pointsIt->to_ostream(os);
	  }
	  os << ") ";
	}
	
	// convert to xml stream
	void Comm3dPointCloudCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<valid>" << getValid() << "</valid>";
		std::vector<CommBasicObjects::CommPosition3d>::const_iterator pointsIt;
		std::vector<CommBasicObjects::CommPosition3d> pointsList = getPointsCopy();
		counter = 0;
		os << indent << "<pointsList n=\"" << pointsList.size() << "\">";
		for(pointsIt=pointsList.begin(); pointsIt!=pointsList.end(); pointsIt++) {
			os << indent << "<points i=\"" << counter++ << "\">";
			pointsIt->to_xml(os, indent);
			os << indent << "</points>";
		}
		os << indent << "</pointsList>";
	}
	
	// restore from xml stream
	void Comm3dPointCloudCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_valid("<valid>");
		static const Smart::KnuthMorrisPratt kmp_pointsList("<pointsList n=\"");
		static const Smart::KnuthMorrisPratt kmp_points("\">");
		
		if(kmp_valid.search(is)) {
			bool validItem;
			is >> validItem;
			setValid(validItem);
		}
		if(kmp_pointsList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommBasicObjects::CommPosition3d pointsItem;
			std::vector<CommBasicObjects::CommPosition3d> pointsList;
			kmp_points.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_points.search(is)) {
					pointsItem.from_xml(is);
					pointsList.push_back(pointsItem);
				}
			}
			setPoints(pointsList);
		}
	}
	
