	const char* ImageParametersCore::getCompiledHash()
	{
		return DomainVisionIDL::REPO_HASH;
	}
	
	void ImageParametersCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void ImageParametersCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "ImageParametersCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t ImageParametersCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.width);
		boost::hash_combine(seed, data.height);
		boost::hash_combine(seed, data.format);
		boost::hash_combine(seed, data.depth);
		boost::hash_combine(seed, data.size);
		
		return seed;
	}
	#endif
	
	// default constructor
	ImageParametersCore::ImageParametersCore()
	:	idl_ImageParameters()
	{  
		setWidth(0);
		setHeight(0);
		setFormat(DomainVision::FormatType());
		setDepth(0);
		setSize(0);
	}
	
	ImageParametersCore::ImageParametersCore(const DATATYPE &data)
	:	idl_ImageParameters(data)
	{  }

	ImageParametersCore::~ImageParametersCore()
	{  }
	
	void ImageParametersCore::to_ostream(std::ostream &os) const
	{
	  os << "ImageParameters(";
	  os << getWidth() << " ";
	  os << getHeight() << " ";
	  getFormat().to_ostream(os);
	  os << getDepth() << " ";
	  os << getSize() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void ImageParametersCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<width>" << getWidth() << "</width>";
		os << indent << "<height>" << getHeight() << "</height>";
		os << indent << "<format>";
		getFormat().to_xml(os, indent);
		os << indent << "</format>";
		os << indent << "<depth>" << getDepth() << "</depth>";
		os << indent << "<size>" << getSize() << "</size>";
	}
	
	// restore from xml stream
	void ImageParametersCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_width("<width>");
		static const Smart::KnuthMorrisPratt kmp_height("<height>");
		static const Smart::KnuthMorrisPratt kmp_format("<format>");
		static const Smart::KnuthMorrisPratt kmp_depth("<depth>");
		static const Smart::KnuthMorrisPratt kmp_size("<size>");
		
		if(kmp_width.search(is)) {
			unsigned int widthItem;
			is >> widthItem;
			setWidth(widthItem);
		}
		if(kmp_height.search(is)) {
			unsigned int heightItem;
			is >> heightItem;
			setHeight(heightItem);
		}
		if(kmp_format.search(is)) {
			DomainVision::FormatType formatItem;
			formatItem.from_xml(is);
			setFormat(formatItem);
		}
		if(kmp_depth.search(is)) {
			unsigned int depthItem;
			is >> depthItem;
			setDepth(depthItem);
		}
		if(kmp_size.search(is)) {
			unsigned int sizeItem;
			is >> sizeItem;
			setSize(sizeItem);
		}
	}
	
